// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./Hub.sol";


contract AssertionRegistry is Ownable {
	Hub public hub;
	IERC20 token;

	address public TRAC_TOKEN_ADDRESS;

	struct AssertionRecord{
		uint256 numberOfEpochs;
		uint256 creationTimestamp;
		address issuer;
		bytes32 signature;
		bytes32 data; // for extensibility
		// TODO: include Token management
	}

	mapping(bytes32 => AssertionRecord) internal assertionRecords;

	// events
	event AssertionCreated(bytes32 indexed assertionId, address indexed assertionIssuer, uint256 numberOfEpochs);
//	event TokensCollected(bytes32 indexed assertionId, address indexed prover, uint256 indexed epoch, uint256 amount);
//	event VerificationFailed(bytes32 indexed assertionId, address indexed prover, uint256 indexed epoch);

	constructor(address hubAddress) {
		require(hubAddress != address(0));
		hub = Hub(hubAddress);
	}

	/* Sender */
	function createAssertionRecord(bytes32 assertionId, address issuer, uint256 numberOfEpochs, bytes32 signature) public {
		require(assertionId != 0, "assertionID hash cannot be zero");
		require(numberOfEpochs != 0, "numberOfEpochs hash cannot be zero");
		require(assertionRecords[assertionId].creationTimestamp == 0, "Assertion already exists!");

		assertionRecords[assertionId].creationTimestamp = block.timestamp;
		assertionRecords[assertionId].issuer = issuer;
		assertionRecords[assertionId].numberOfEpochs = numberOfEpochs;
		assertionRecords[assertionId].signature = signature;

		emit AssertionCreated(assertionId, issuer, numberOfEpochs);
	}


	/* Receiver */
//	function submitHoldingProof(bytes32 assertionID, bytes32 epochAssertionProof, uint256 epochNumber)
//	public {
//		require(assertionID != 0, "assertionID hash cannot be zero");
//		require(epochAssertionProof != 0, "epochAssertionProof hash cannot be zero");
//
//		if (verifyAssertionProof(assertionID, epochAssertionProof)){
//			emit TokensCollected(assertionID,msg.sender,epochNumber, 123);
//			} else {
//				emit VerificationFailed(assertionID,msg.sender,epochNumber);
//			}
//
//	}
//
//	function verifyAssertionProof(bytes32 assertionID, bytes32 epochAssertionProof)
//		public returns(bool proofValid){
//		return true;
//
//	}



	function getAssertionIssuer(bytes32 assertionId)
	public view returns(address issuer){
		return assertionRecords[assertionId].issuer;
	}
	function getAssertionNumberOfEpochs(bytes32 assertionId)
	public view returns(uint256 numberOfEpochs){
		return assertionRecords[assertionId].numberOfEpochs;
	}
	function getAssertionCreationTimestamp(bytes32 assertionId)
	public view returns(uint256 numberOfEpochs){
		return assertionRecords[assertionId].creationTimestamp;
	}

}
