pragma solidity >=0.6.0 <0.8.0;

import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-solidity/contracts/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/access/Ownable.sol";


contract AssertionRegistry is Ownable {
	using SafeMath for uint256;
	IERC20 token;

	address public TRAC_TOKEN_ADDRESS;

	struct AssertionRecord{
		uint256 numberOfEpochs;
		uint256 creationTimestamp;
		address issuer;
		bytes32 data; // for extensibility
		// TODO: include Token management
	}

	mapping(bytes32 => AssertionRecord) internal assertionRecords;

	// events
	event AssertionCreated(bytes32 indexed assertionId, address indexed assertionIssuer, uint256 numberOfEpochs);
//	event TokensCollected(bytes32 indexed assertionId, address indexed prover, uint256 indexed epoch, uint256 amount);
//	event VerificationFailed(bytes32 indexed assertionId, address indexed prover, uint256 indexed epoch);

	constructor() public {

	}

	/* Sender */
	function createAssertionRecord(bytes32 assertionId, address issuer, uint256 numberOfEpochs) public {
		require(assertionId != 0, "assertionID hash cannot be zero");
		require(numberOfEpochs != 0, "numberOfEpochs hash cannot be zero");
		require(assertionRecords[assertionId].creationTimestamp== 0, "Assertion already exists!");

		assertionRecords[assertionId].creationTimestamp = block.timestamp;
		assertionRecords[assertionId].issuer = issuer;
		assertionRecords[assertionId].numberOfEpochs = numberOfEpochs;

		emit AssertionCreated(assertionId, issuer, numberOfEpochs);
	}


