	function getAssertionIssuer(bytes32 assertionId)
	public view returns(address issuer){
		return assertionRecords[assertionId].issuer;
	}
	function getAssertionNumberOfEpochs(bytes32 assertionId)
	public view returns(uint256 numberOfEpochs){
		return assertionRecords[assertionId].numberOfEpochs;
	}
	function getAssertionCreationTimestamp(bytes32 assertionId)
	public view returns(uint256 numberOfEpochs){
		return assertionRecords[assertionId].creationTimestamp;
	}
	function getAssertionTask(bytes32 assertionID, uint256 epoch)
	public view returns(uint256 task){
		return block.number;
	}

}
