// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "openzeppelin-solidity/contracts/token/ERC20/IERC20.sol";
import "openzeppelin-solidity/contracts/utils/math/SafeMath.sol";
import "openzeppelin-solidity/contracts/access/Ownable.sol";


// mock contract for now, we will add the hub and the rest later
contract DKGcontract is Ownable {


	using SafeMath for uint256;
	IERC20 token;

	address public TRAC_TOKEN_ADDRESS;

	struct AssertionRecord{
		// bytes32 assertionID; // hash (multihash) of the assertion
		uint256 assertionTimeToLive;
		uint256 numberOfEpochs;
		uint256 creationTime;
		address issuer;
		bytes32 rootHash;
		bytes32 data; // for extensibility
		// TODO: include Token management
	}

	mapping(bytes32 => AssertionRecord) internal assertionRecord; // assertionRecord[assertionID] , formerly offer[offerID]
	mapping(bytes32 => address) internal assertionIssuer; // mapping assertionID -> assertionIssuer


	// events
	event TokenAddressSet(address indexed token);
	event AssertionCreated(bytes32 indexed assertionID, bytes32 indexed rootHash, address indexed assertionIssuer, uint256 numberOfEpochs, uint256 assertionTimeToLive);
	event TokensCollected(bytes32 indexed assertionID, address indexed prover, uint256 indexed epoch, uint256 amount);
	event VerificationFailed(bytes32 indexed assertionID, address indexed prover, uint256 indexed epoch);



	constructor(address token_address)  public {
		require(token_address!=address(0));
		token = IERC20(token_address);
		TRAC_TOKEN_ADDRESS = token_address;

		emit TokenAddressSet(token_address);

	}

	/* DC */
	function createAssertionRecord(bytes32 assertionID, bytes32 rootHash, address issuer, uint256 numberOfEpochs, uint256 assertionTimeToLive) public {
		require(assertionID != 0, "assertionID hash cannot be zero");
		require(rootHash != 0, "rootHash hash cannot be zero");
		require(numberOfEpochs != 0, "numberOfEpochs hash cannot be zero");
		require(assertionTimeToLive != 0, "assertionTimeToLive hash cannot be zero");
		require(assertionRecord[assertionID].creationTime==0, "Assertion already exists!");

		// transfer token to contract here (next version)

		assertionRecord[assertionID].numberOfEpochs = numberOfEpochs;
		assertionRecord[assertionID].assertionTimeToLive = assertionTimeToLive;
		assertionRecord[assertionID].creationTime = block.timestamp;
		assertionRecord[assertionID].rootHash = rootHash;
		assertionRecord[assertionID].issuer = issuer;
		assertionIssuer[assertionID] = issuer;

		emit AssertionCreated(assertionID,rootHash,issuer,numberOfEpochs,assertionTimeToLive);
	}


	/* DH */

	function submitHoldingProof(bytes32 assertionID, bytes32 epochAssertionProof, uint256 epochNumber) 
	public {
		require(assertionID != 0, "assertionID hash cannot be zero");
		require(epochAssertionProof != 0, "epochAssertionProof hash cannot be zero");

		if (verifyAssertionProof(assertionID, epochAssertionProof)){
			emit TokensCollected(assertionID,msg.sender,epochNumber, 123);
			} else {
				emit VerificationFailed(assertionID,msg.sender,epochNumber);
			}

	}



	function verifyAssertionProof(bytes32 assertionID, bytes32 epochAssertionProof) 
		public returns(bool proofValid){
		return true;

	}



	function getAssertionIssuer(bytes32 assertionID)
	public view returns(address creator){
		return assertionIssuer[assertionID];
	}
	function getAssertionRootHash(bytes32 assertionID)
	public view returns(bytes32 rootHash){
		return assertionRecord[assertionID].rootHash;
	}
	function getAssertionTimeToLive(bytes32 assertionID)
	public view returns(uint256 timeToLive){
		return assertionRecord[assertionID].assertionTimeToLive;
	}
	function getAssertionNumberOfEpochs(bytes32 assertionID)
	public view returns(uint256 numberOfEpochs){
		return assertionRecord[assertionID].numberOfEpochs;
	}

	function getAssertionTask(bytes32 assertionID, uint256 epoch)
	public view returns(uint256 task){
		return block.number;
	}

}
